export const validation=(obj)=>{
    //{ username: '', password: '' }
    const respuesta = {};
    const mailValidate= '^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+\.[a-zA-z]{2,3}$';
    const passValidate= '^(?=\w*\d)(?=\w*[A-Z])(?=\w*[a-z])\S{6,10}$';
    if(!mailValidate.test(obj.username))respuesta.username='debe ser un email válido.'
    if(!passValidate.test(obj.password))respuesta.password='El password debe tener de 6'

}
