import Card from './components/card/Card.jsx';
import Cards from './components/cards/Cards.jsx';
import Nav from './components/nav/Nav.jsx';
import About from './components/about/About.jsx';
import Details from './components/details/Detail.jsx';
import styles from './app.module.css';
import { useState } from 'react';
import { Routes, Route } from 'react-router-dom';

function App() {
  const [characters, setCharacters] = useState([]);

  const onSearch = (character) => {
    fetch(`https://rickandmortyapi.com/api/character/${character}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.name) {
          setCharacters((oldChars) => [...oldChars, data]);//
        } else {
          window.alert('No hay personajes con ese ID');
        }
      });
  }

  const onClose = (name) => {
    let result = characters.filter(elemento => elemento.name !== name)
    setCharacters(result);
  }
  return (

    <div className={styles.App}>
      <Nav onSearch={onSearch} />
      <Routes>
      <Route exact path="/Home" element={<Cards characters={characters} onClose={onClose} />}></Route>
        <Route exact path="/Home" element={<Cards characters={characters} onClose={onClose} />}></Route>
        <Route path="/about" element = {<About />}></Route>
        <Route path="/detail/:detailId" element ={<Details />}></Route>
      </Routes>
    </div>
  )
}
//className={styles.barra}

export default App
