import React from 'react';
import styles from './searchbar.module.css'

export default function SearchBar(props) {
   const [estado, setEstado]= useState('')
   const cambiarEstado = (evento)=>{
      setEstado(estado= evento.target.value)
   }
   return (
      <div className={styles.buscar}>
         <input className={styles.recuadro} value={estado} type='search' onChange={cambiarEstado} />
         <button className={styles.boton} onClick={props.onSearch}>Agregar</button>
      </div>
   );
}

function onSearch(valor) {
   alert("buscar")
}
