import React from 'react';
import styles from './Form.module.css';
import { useState, useEffect } from 'react';
import validation  from './validation';

function Form() {

    const [userData, setUserData] = useState({ username: '', password: '' });
    const [errors, setErrors] = useState({ username:'', password:''})
    const handleInputChange=(e)=>{
        setUserData({...userData,[e.target.name]:e.target.value})
    }
    useEffect(()=>{
      setErrors(validation(userData));
    }, [userData]);

    useEffect(()=>{
      mostrar()
    },[errors]);

    const mostrar=()=>{if (Object.values(errors).length >0)styles.boton;
      else styles.noMostrar;
    }

  return (
    <div className={styles.formulario}>
        <label className={styles.user}>User: 
        <input className={styles.inp} name="username" onChange={handleInputChange} type="text" value={userData.username} /></label>
        <span className={styles.aviso}>{errors.username}</span>
        <label className={styles.user}>Pass:
        <input className={styles.inp} name="password" onChange={handleInputChange} type="password" value={userData.password} /></label>
        <span className={styles.aviso}>{errors.password}</span>
        <button className={mostrar}>LOGIN</button>

    </div>
  )
}

export default Form
